angular.module("schemaForm").run(["$templateCache", function($templateCache) {$templateCache.put("directives/decorators/bootstrap/arrayedit/angular-schema-form-arrayedit.html","<div sf-array=\"form\" class=\"schema-form-array {{form.htmlClass}}\" ng-model=\"$$value$$\" ng-model-options=\"form.ngModelOptions\">\n    <label class=\"control-label\" ng-show=\"showTitle()\">{{ form.title }} </label>\n    <div ng-bind-html=\"form.description\"></div>\n    <div class=\"clearfix schema-form-array-header\">\n        <div class=\"pull-left\">\n            <label class=\"schema-form-array-item-title\">{{form.schema.items.title}}</label>\n            <div class=\"schema-form-array-required\" ng-bind-html=\"modelArray.length + \' of \' +form.schema.arrayrequired\"></div>\n        </div>\n        <div class=\"pull-right\">   \n            <button ng-hide=\"form.readonly || form.add === null\" ng-click=\"appendToArray();editable[modelArray.length-1]=true\" type=\"button\" class=\"btn {{ form.style.add || \'btn-default\' }}\">\n                <i class=\"glyphicon glyphicon-plus\"></i> {{ form.add || \'Add\'}}\n            </button>\n        </div>\n        </div>\n    <div class=\"list-group\" ng-model=\"modelArray\" ui-sortable=\"\">\n        <div class=\"list-group-item {{form.fieldHtmlClass}}\" ng-repeat=\"item in modelArray track by $index\">\n            <div ng-show=\"editable[$index]\">\n                <sf-decorator ng-init=\"arrayIndex = $index\" form=\"copyWithIndex($index)\" ng-if=\"true\"></sf-decorator>\n            </div>\n            <div ng-show=\"!editable[$index]\">\n                <div ng-repeat=\"(key, value) in item\" class=\"form-group\">\n                    <div class=\"col-sm-2\"><b style=\"text-transform:capitalize\">{{key.toLowerCase()}}  :</b></div>\n                    <div class=\"col-sm-10\">{{value}} &nbsp;</div>\n                </div>\n            </div>\n            <div class=\"schema-form-array-buttons\" ng-init=\"(modelArray.length==1)? editable[0]=true : \'\' \">\n                <button class=\"btn btn-warning\" ng-show=\"!editable[$index]\" ng-click=\"editable[$index]=true;\">Edit</button>\n                <button class=\"btn btn-success\" ng-show=\"editable[$index]\" ng-click=\"editable[$index]=false\">Done</button>\n                <button class=\"btn btn-danger\" ng-hide=\"form.readonly || form.remove === null\" ng-click=\"deleteFromArray($index)\">Delete</button>    \n            </div>\n        </div>\n    </div>\n\n    <div class=\"help-block\" ng-show=\"(hasError() && errorMessage(schemaError())) || form.description\" ng-bind-html=\"(hasError() && errorMessage(schemaError()) || \'\')\"></div>\n</div>");}]);
angular.module('schemaForm').config(
    ['schemaFormProvider', 'schemaFormDecoratorsProvider', 'sfPathProvider',
        function(schemaFormProvider, schemaFormDecoratorsProvider, sfPathProvider) {
          //Add Mapping
          schemaFormDecoratorsProvider.addMapping(
            'bootstrapDecorator',
            'array',
            'directives/decorators/bootstrap/arrayedit/angular-schema-form-arrayedit.html'
          );
        }
    ]);
